import '../index.css';
import Header from './Header.js';
import Main from './Main.js';
import Footer from './Footer.js';
import PopupWithForm from './PopupWithForm.js';
import { useState } from 'react';

function App() {

    const [isEditProfileOpen, setIsEditProfileOpen] = useState(false);
    const [isAddPlaceOpen, setIsAddPlaceOpen] = useState(false);
    const [isEditAvatarOpen, setIsEditAvatarOpen] = useState(false);

    const handleEditProfile = () => {
        console.log('edit profile');
        setIsEditProfileOpen(!isEditProfileOpen);
    }

    const handleAddPlace = () => {
        console.log('add place');
        setIsAddPlaceOpen(!isAddPlaceOpen);
    }

    const handleEditAvatar = () => {
        console.log('edit avatar');
        setIsEditAvatarOpen(!isEditAvatarOpen);
    }

    const closeAllPopups = () => {
        console.log('all popups are closed');
        setIsEditProfileOpen(false);
        setIsEditAvatarOpen(false);
        setIsAddPlaceOpen(false);
    }

    return (
        <div class="page">
            <Header />
            <Main onEditProfile={handleEditProfile} onAddPlace={handleAddPlace} onEditAvatar={handleEditAvatar}/>
            <Footer />
            <PopupWithForm name='profile-edit' isOpen={isEditProfileOpen} onClose={closeAllPopups}/>
            {/* <PopupWithForm name='new-place' isOpen={isAddPlaceOpen} onClose={closeAllPopups}/>
            <PopupWithForm name='edit-avatar' isOpen={isEditAvatarOpen} onClose={closeAllPopups}/> */}

            <div class='popup popup_type_image'>
                <div class="popup__container popup__container_type_image">
                    <button class="popup__close-button" type="button" id="pi-close"></button>
                    <img class="popup__image" src="#" alt="" />
                    <p class="popup__cite"></p>
                </div>
                <div class="popup__overlay popup__overlay_type_big-image"></div>
            </div>

        </div>
  )};

export default App;
