// import avatarImage from '../images/image.jpg'
import avatarEditIcon from '../images/avatar-edit-button.svg'
import { useEffect, useState } from 'react'
import { api } from './utils/utils.js'
// import { useEffect, useState } from 'react';




function Main(props){
    const [cards, setCards] = useState([]);
    const [avatar, setAvatar] = useState();

    useEffect(() => {
        api.getInitialCards()
            .then((res) => setCards(res));

        api.getUserData()
            .then((res) => setAvatar(res.avatar));
    }, [])

    return(
        <main className="main">
            <section className="profile">
                <div className="profile__avatar-container">
                    <img className="profile__avatar" src={avatar} alt="аватар"/>
                    <img className="profile__avatar-edit-button" src={avatarEditIcon} onClick={props.onPopup} alt="Иконка редактирования аватарки" id="edit-avatar"/>
                    <div className="profile__overlay"></div>
                </div>
                <div className="profile__info">
                    <div className="profile__name-button-container">
                        <h2 className = "profile__name">Жак-Ив Кусто</h2>
                        <button className="profile__edit-button" type="button" onClick={props.onPopup} id="profile-edit"></button>
                    </div>
                    <h3 className="profile__description">Иследователь</h3>
                </div>
                <button className="profile__add-button" type="button" onClick={props.onPopup} id="new-place"></button>
            </section>

            <section className="elements">
                {cards?.map((item, index) => {
                    return(
                    <div class="elements__element" key={index}>
                    <button class="elements__remove-button" type="button"></button>
                    <div class="elements__image-container" onClick={props.onCardClick}>
                    <img class="elements__image" src={item.link} alt={item.name}/>
                    </div>
                    <div class="elements__group">
                        <h2 class="elements__name">{`${item.name}`}</h2>
                        <div class="elements__like-container">
                            <button class="elements__like-button" type="button" id="like-button"></button>
                            <p class="elements__like-counter">{item.likes.length}</p>
                        </div>
                    </div>
                    </div>
                    )
                })}
            </section>
        </main>
    )
}

export default Main;
