import '../index.css';
import Header from './Header.js';
import Main from './Main.js';
import Footer from './Footer.js';
import PopupWithForm from './PopupWithForm.js';
import ImagePopup from './ImagePopup.jsx';
import { useState } from 'react';

function App() {

    const [popupState, setPopupState] = useState({ isOpen: false, type: ''})
    const [selectedCard, setSelectedCard] = useState('');

    const handleCardClick = (e) => {
        setSelectedCard(e.currentTarget.id);

    }

    const handlePopupOpen = (e) => {
        setPopupState({ isOpen: true, type: e.currentTarget.id})
    }

    const closeAllPopups = (e) => {
        setPopupState({ isOpen: false, type: e.currentTarget.id })
    }


    return (
        <div class="page">
            <Header />
            <Main onPopup={handlePopupOpen}/>
            <Footer />
            <PopupWithForm name={popupState.type} isOpen={popupState.isOpen} onClose={closeAllPopups} onCardClick={handleCardClick}/>
            <ImagePopup card={selectedCard} />
            <div class='popup popup_type_image'>
                <div class="popup__container popup__container_type_image">
                    <button class="popup__close-button" type="button" id="pi-close"></button>
                    <img class="popup__image" src="#" alt="" />
                    <p class="popup__cite"></p>
                </div>
                <div class="popup__overlay popup__overlay_type_big-image"></div>
            </div>

        </div>
  )};

export default App;
